var settings = require('rah.utils').settings;

module.exports.model = function (seq) {
    return {
        columns: {
            //nome do módulo
            name: { type: seq.STRING, unique: true, allowNull: false },
            //decrição do módulo
            desc: { type: seq.STRING, unique: true, allowNull: false },
            data: {
                type: seq.JSONB,
                fields: [{
                    name: 'routes'
                }]
            }
        },
        options: {
            tableName: 'tb_modules', 
            timestamps: true            
        }
    }
}