
var int = require('rah.utils/functions/int.index.js');


function get(model, id) {
    return new Promise(function (resolve, reject) {
        id = int.TryParseInt(id, null);
        if (id === null) return reject('Invalid Id');

        model.findOne({ where: { id: id } }).then(function (result) {
            resolve(result);
        }).catch(function (err) {
            reject(Error(err));
        });
    });
};

function getAll(model) {
    return new Promise(function (resolve, reject) {
        model.findAll().then(function (result) {
            resolve(result);
        }).catch(function (err) {
            reject(Error(err));
        });
    });
};

function create(db, modelName) {
    var model = db.model(modelName);

    return {
        get: function(id) { return get(model, id) },
        getAll: function() { return getAll(model) }
    }
}


module.exports = {
    create
}